DROP TABLE IF EXISTS user;
CREATE TABLE user (
    id INTEGER UNIQUE PRIMARY KEY AUTOINCREMENT,
    email VARCHAR UNIQUE,
    password VARCHAR,
    firstname VARCHAR,
    lastname VARCHAR,
    salutation VARCHAR,
    role VARCHAR
);


DROP TABLE IF EXISTS product;
CREATE TABLE product (
    id INTEGER UNIQUE PRIMARY KEY AUTOINCREMENT,
    title VARCHAR,
    description TEXT,
    picture VARCHAR
);


