INSERT INTO product (title, description, picture) VALUES ('MOZZARELLA LOVER BEEF', '„Mamma mia“ – so lecker ist der neue Mozzarella Beef. Hol dir jetzt den neuen Burger mit knusprigem Mozzarella-Patty, cremiger Pesto-Sauce und original flame-grilled Beef. Auch als Double erhältlich. Nur bei Burger King.', 'https://www.burgerking.de/tmp/image-thumb__26860__product_detail/tmp-1618933409-17151-de.png');
INSERT INTO product (title, description, picture) VALUES ('FREAKINGDEL', 'Hup Hup Holland Style! Mit dem FreaKingdel holen wir dir die niederländische Spezialität mit saftigen Fleischröllchen aus Beef und Chicken zu dir nach Hause.', 'https://www.burgerking.de/001_general/products/productimages/burger/neue_VI/image-thumb__27120__product_detail/FreaKingdel-01.png');
INSERT INTO product (title, description, picture) VALUES ('STEAKHOUSE JR.', 'Der Steakhouse Jr. – genauso lecker wie sein großer Bruder, nur ein bisschen kleiner. Mit flame-grilled Beef, würziger BBQ Sauce, knusprigem Bacon und herzhaften Röstzwiebeln.', 'https://www.burgerking.de/001_general/products/productimages/burger/neue_VI/image-thumb__26611__product_detail/SteakhouseJr-01.png');
INSERT INTO product (title, description, picture) VALUES ('MOZZARELLA LOVER CHICKEN', '„Mamma mia“ – so lecker ist der neue Mozzarella Chicken. Hol dir jetzt den neuen Burger mit knusprigem Mozzarella-Patty, cremiger Pesto-Sauce und dem leckeren Crunchy Chicken Deluxe Patty. Nur bei Burger King.', 'https://www.burgerking.de/001_general/products/productimages/burger/image-thumb__26853__product_detail/MozzarellaChicken-02.png');
INSERT INTO product (title, description, picture) VALUES ('PLANT-BASED NUGGETS', 'Bei Burger King® gibt es die knusprigen Plant-based* Nuggets von The Vegetarian Butcher – Zero Chicken und trotzdem voller King Nugget-Flavor. Bei The Vegetarian Butcher dreht sich alles darum, das Genusserlebnis von Fleisch nachzubauen – ohne Kompromisse im Geschmack! Glaubst du nicht? Try it to believe it!', 'https://www.burgerking.de/tmp/image-thumb__27880__product_detail/tmp-1623047902-17998-de.png');
