INSERT INTO product (title, description, picture) VALUES ('KING SUNDAE SMARTIES®', 'Zum Dahinschmelzen: der neue King Sundae Smarties® mit cremigem Softeis und bunten Smarties®. Sorgt für Sommerfeeling, egal zu welcher Jahreszeit. Auch mit Cornetto® Himbeersauce, Cornetto® Karamellsauce oder Schokosauce erhältlich.', 'https://www.burgerking.de/001_general/products/productimages/sweets/neue_VI/image-thumb__26806__product_detail/KingSundae_Smarties-01.png');
INSERT INTO product (title, description, picture) VALUES ('KING FUSION HAVE IT YOUR WAY®', 'Du hättest am liebsten Oreo®, Smarties® und Cornetto® Himbeersauce zu deinem cremigen Softeis? Kein Problem: Mit dem neuen King Fusion Have It Your Way kannst du dir aus insgesamt 8 verschiedenen Toppings beliebig viele Zutaten aussuchen. Und deinen King Fusion ganz nach deinem Geschmack genießen.', 'https://www.burgerking.de/tmp/image-thumb__27615__product_detail/tmp-1622038173-17314-de.png');
INSERT INTO product (title, description, picture) VALUES ('KING FUSION SMARTIES®', 'So schmeckt der Sommer: cremiges Softeis kombiniert mit herrlicher Schokosauce und farbenfrohen Smarties®. Mit unserem neuen King Fusion Smarties® ist gute Laune vorprogrammiert.', 'https://www.burgerking.de/001_general/products/productimages/sweets/neue_VI/image-thumb__26794__product_detail/KingFusion_Smarties-01.png');
